// Initialize variables
const MAX_LENGTH = 4000; // More conservative limit
let fullSummary = "ðŸ“¬ *Unread Email Digest* ðŸ“¬\n\n";
let emailIds = [];
let importantCount = 0;
let starredCount = 0;

// Process each email
items.forEach((item, index) => {
  const from = item.json?.From?.replace(/<[^>]+>/g, '').trim() || "Unknown";
  const subject = item.json?.Subject || "No Subject";
  const snippet = item.json?.snippet?.substring(0, 100) || "No preview";
  const isImportant = item.json?.labelIds?.includes('IMPORTANT');
  const isStarred = item.json?.labelIds?.includes('STARRED');

  // Build the full summary
  fullSummary += `
âœ‰ï¸ *${index + 1}. ${subject}*
   ðŸ‘¤ From: ${from}
   ðŸ“„ ${snippet}${isStarred ? ' â­' : ''}${isImportant ? ' â—' : ''}
  `;

  if (item.json?.id) emailIds.push(item.json.id);
  if (isImportant) importantCount++;
  if (isStarred) starredCount++;
});

// Add footer
fullSummary += `\nðŸ“Š *Stats:* ${items.length} total â€¢ ${importantCount} important â€¢ ${starredCount} starred`;

// Return single message with all data
return [{
  json: {
    fullSummary, // Now contains complete digest
    emailIds,   // All email IDs in one array
    stats: {
      total: items.length,
      important: importantCount,
      starred: starredCount
    }
  }
}];

// Process email IDs from ALL messages
const allIds = [];

// First check if we have direct ID array (from previous node)
if (Array.isArray($input.all()[0]?.json?.emailIds)) {
  allIds.push(...$input.all()[0].json.emailIds);
} 
// Otherwise collect IDs from individual items
else {
  $input.all().forEach(item => {
    if (item.json?.id) {
      allIds.push(item.json.id);
    }
  });
}

// Return individual ID objects
if (allIds.length > 0) {
  return allIds.map(id => ({ json: { id } }));
} else {
  // Ensure we always return valid data
  return [{ json: { notice: "No email IDs found to mark as read" } }];
}
